package com.dss;

public class Swap_String {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s = "madam";
		String s2 = "mam";
		String s3 = "";

		s3 = s;
		s = s2;
		s2 = s3;
		System.out.println(s);
		System.out.println(s2);
	}

}
